import React, { useEffect, useState } from "react";
import "./ListSearch.css";
import axios from "axios";
import { Base_Url } from "../config/config";

const ListSearch = (prop) => {
  const [productList, setProductList] = useState([]);
  const [loading, setLoading] = useState(false);
  const [searchList, setSearchList] = useState([]);

  useEffect(() => {
    // setLoading(true);
    console.log("PPPPPP", prop.search, productList.length);
    if (!productList.length) {
      loadProducts();
    }
    setSearchList(
      productList.filter((item) =>
        item.productName.match(new RegExp(prop.search, "i")) ||  item.category.match(new RegExp(prop.search, "i"))
      )
    );
  }, [prop.search]);

  const loadProducts = () => {
    axios
      .get(`${Base_Url}/allProducts`)
      .then((result) => {
        console.log("res loading", result);
        setLoading(false);
        setProductList(result.data);
      })
      .catch((err) => {
        setLoading(false);
      });
  };

  return (
    <div className="list-search position-absolute w-75 bg-white z-1">
      {loading ? (
        <div className="d-flex justify-content-center align-items-center spinner">
          <div className="spinner-border" role="status"></div>
        </div>
      ) : (
        ""
      )}

      {searchList.length ? (
        <ul className="list-ul">
          {searchList.map((item, index) => {
            return (
              <><li key={index} value={item.productName}>
                {item.productName}
              </li><li value={item.category}>
                  {item.category}
                </li></>
            );
          })}
        </ul>
      ) : 
      <ul className="list-ul">
        <li>----  No products found  ----</li>
        </ul>
      }
    </div>
  );
};

export default ListSearch;
