import React, { useEffect, useState } from "react";
import axios from "axios";
import Swal from "sweetalert2";
import "./styles.css";
import { Base_Url } from "../config/config";

export const Topsale = () => {
  const [salesList, setSaleList] = useState([]);
  const [loading, setLoading] = useState(false);
  const token = 'Bearer '+JSON.stringify(localStorage.getItem("token") )
  const HEADER = {
    headers:{
      "Content-Type": "application/json",
      "Authorization":  token
    }
  }

  useEffect(() => {
    setLoading(true);
    loadSales();

  }, []);

  const loadSales = () => {

    axios
      .get(`${Base_Url}/topSales`,HEADER)
      .then((result) => {

        setSaleList(result.data);
        setLoading(false);
      })
      .catch((err) => {
        console.log('loading',err.response.data)
        setLoading(false);
        //Swal.fire(err.response.data.error);
      });
  };

  return (
    <div className="container mt-5">
      {loading ? (
        <div className="d-flex justify-content-center align-items-center spinner">
          <div className="spinner-border" role="status"></div>
        </div>
      ) : (
        ""
      )}
      <h3 className="text-center fw-bold pb-4">TOP 5 SALES</h3>
      <table className="table">
        <thead>
          <tr>
            <th scope="col">#</th>
            <th scope="col">Sales Id:</th>
            <th scope="col">Product Name</th>
            <th scope="col">Quantity</th>
            <th scope="col">Sale Amount</th>
          </tr>
        </thead>
        <tbody className="table-group-divider">
          {salesList.map((item, index) => {
            return (
              <tr key={index}>
                <th scope="row">{index + 1}</th>
                <td>{item._id}</td>
                <td>{item.productName}</td>
                <td>{item.Quantity}</td>
                <td>{item.Amount}</td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
};
