import React, { useState } from "react";
import axios from "axios";
import Swal from "sweetalert2";
import "./styles.css";
import { Base_Url } from "../config/config";

const Addsale = () => {

  const HEADER = {
    headers:{
      "Content-Type": "application/json",
      "Authorization": 'Bearer ' + JSON.parse(localStorage.getItem("token") )  
    }
  }
  const [productName, setproductName] = useState("");
  const [quantity, setQuantity] = useState(0);
  const [amount, setAmount] = useState(0);
  const [loading, setLoading] = useState(false);

  const submitForm = (event) => {
    event.preventDefault();
    setLoading(true);
    console.log(productName,quantity,amount)
    const data = {
      productName: productName,
      Quantity: quantity,
      Amount: amount,
    };
    axios
      .post(`${Base_Url}/addSale`,data, HEADER)
      .then((result) => {
          console.log(result)
            setLoading(false);
            Swal.fire(result.data.message);
            setproductName("")
            setQuantity(0)
            setAmount(0)
      })
      .catch((err) => {
        console.log('err',err)
        setLoading(false);
       // Swal.fire(err.response.data.error);
      });
  };

  return (
    <div className="container mt-5">
      <h3 className="text-center fw-bold">ADD SALE ENTRY</h3>
      { loading ? <div className="d-flex justify-content-center align-items-center spinner">
        <div className="spinner-border" role="status"></div>
      </div>: '' }
      <form className="border p-3 shadow-sm" onSubmit={(event) => submitForm(event)}>
        <div className="mb-3">
          <label className="form-label">Product Name</label>
          <input
            type="text"
            className="form-control"
            onChange={(e) => setproductName(e.target.value)}
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Quantity</label>
          <input
            type="numbet"
            className="form-control"
            onChange={(e) => setQuantity(e.target.value)}
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Amount</label>
          <input
            type="number"
            className="form-control"
            onChange={(e) => setAmount(e.target.value)}
          />
        </div>

        <button type="submit" className="btn btn-primary w-100">
          Submit
        </button>
      </form>
    </div>
  );
};

export default Addsale;
