import React, { useEffect, useState } from "react";
import "./ListSearch.css";
import axios from "axios";
import { Base_Url } from "../config/config";

const ListSearch = () => {
  const [productList, setProductList] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    setLoading(true);
    loadProducts();
  }, []);

  const loadProducts = () => {
    axios
      .get(`${Base_Url}/allProducts`)
      .then((result) => {
        console.log("res", result);
        setLoading(false);
        setProductList(result.data);
      })
      .catch((err) => {
        setLoading(false);
      });
  };

  return (
    <div className="list-search position-absolute w-75 bg-white z-1">
      {loading ? (
        <div className="d-flex justify-content-center align-items-center spinner">
          <div className="spinner-border" role="status"></div>
        </div>
      ) : (
        ""
      )}
      <input type="text" list="data" className="w-100"/>
      <datalist list="data">
      <option  value="hello" id="data">hello</option>
      </datalist>

      {/* <datalist id="data">
      {productList.slice(0,4).map((item, index) => {
                return (
          <option key={index} value={item.productName} >{item.productName}</option>
        )})}
      </datalist> */}
    </div>
  );
};

export default ListSearch;
