import React from 'react'
import { useLocation } from 'react-router-dom';

export const Product = () => {

  const [product, setProduct] = useState({});

  const location = useLocation();
  const { state } = location;
  const [loading, setLoading] = useState(false);
  const id = state.id
  const HEADER = {
    headers:{
      "Content-Type": "application/json",
      "Authorization": 'Bearer ' + JSON.parse(localStorage.getItem("token") )  
    }
  }

  useEffect(() => {
    setLoading(true);
    loadProduct();

  }, []);

  const loadProduct = () => {

    axios
      .get(`${Base_Url}/product`+id,HEADER)
      .then((result) => {

        setProduct(result.data);
        setLoading(false);
      })
      .catch((err) => {
        console.log('loading',err.response.data)
        setLoading(false);
      });
  };

  return (
    <div>
        <div className="main container mt-3 pb-3">
            <div className="row gy-2">
                <div className="col-12 col-md-4 border border-1 border-secondary pb-3" style={{"height":"75vh"}}>
                    <img src="./images/kids/k1.jpg" height="100%" width="100%" style={{"objectFit": "contain"}} />

                </div>

                <div className="col-12 col-md-4">
                    <div className="card">
                        <h3 className="card-header text-center">{product.productName}</h3>
                        <div className="card-body">
                            <div className="d-flex justify-content-between">
                                <p className="m-1">reviews</p>
                                <p className="m-1">$15</p>
                            </div>
                            <hr className="m-1" />
                            <div className="d-flex justify-content-between">
                                <p className="m-1">price</p>
                                <p className="m-1">$5</p>
                            </div>
                            <hr className="m-1" />
                            <div className="d-flex justify-content-between fw-bold">
                                <p className="m-1">Description</p>
                                <p className="m-1">$20</p>
                            </div>

                        </div>
                    </div>
                </div>

                <div className="col-12 col-md-4">
                    <div className="card">
                        <h3 className="card-header text-center">Summary</h3>
                        <div className="card-body">
                            <div className="d-flex justify-content-between">
                                <p className="m-1">Cost</p>
                                <p className="m-1">$15</p>
                            </div>
                            <hr className="m-1" />
                            <div className="d-flex justify-content-between">
                                <p className="m-1">Shipping</p>
                                <p className="m-1">$5</p>
                            </div>
                            <hr className="m-1" />

                            <div className="text-center">
                                <a href="#" className="btn btn-warning">Add to Cart</a>
                            </div>
                        </div>
                    </div>
                </div>

            </div>

        </div>
    </div>
  )
}
