import React from "react";
import { Link } from "react-router-dom";
import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import Swal from "sweetalert2";
import { Base_Url } from "../config/config";
import axios from "axios";

export const Dashboard = () => {
    const [name, setName] = useState("");
  const [productList, setProductList] = useState([]);
  const [loading, setLoading] = useState(false);
  const [category, setCategory] = useState("DEFAUL");
  const [price, setPrice] = useState(0);
  const [picture, setPicture] = useState("");
  const [gender, setGender] = useState("DEFAULT");
  const [description, setDescription] = useState("");
  const HEADER = {
    headers: {
      "Content-Type": "application/json",
      Authorization: "Bearer " + JSON.parse(localStorage.getItem("token")),
    },
  };

  useEffect(() => {
    setLoading(true);
    loadProducts();
  }, []);

  const loadProducts = () => {
    axios
      .get(`${Base_Url}/allProducts`, HEADER)
      .then((result) => {
        console.log("res", result);

        setProductList(result.data);
        setLoading(false);
      })
      .catch((err) => {
        // console.log("loading", err.response.data);
        setLoading(false);
        //Swal.fire(err.response.data.error);
      });
  };
  const formSubmitted = (event) => {
    console.log('nnn',name)
    event.preventDefault();
    if (
      name === "" ||
      category === "" ||
      price === "" ||
      picture === "" ||
      gender === "" ||
      description === ""
    ) {
      Swal.fire("Fields should not be empty");
      return;
    }

    setLoading(true);
    let product = {
      productName: name,
      gender: gender,
      productPrice: price,
      productPic: picture,
      category: category,
      description: description,
    };
    axios
      .post(`${Base_Url}/addProduct`, product, HEADER)
      .then((result) => {
        setLoading(false);
        if (result.status === 200) {
          Swal.fire({
            title:result.data.message,
          icon: "success"
        }).then(()=>{
          console.log("sub");
            setName("")
            setCategory("DEFAUL")
            setDescription("")
            setGender("DEFAULT")
            setPrice("") 
            setPicture("")
          })

        }
      })
      .catch((err) => {
        setLoading(false);
        Swal.fire({
          icon: "error",
          title: err.response.data.error,
        });
      });
  };

  const editProduct = (item)=>{
      console.log(item)
      setName(item.productName);
      setCategory(item.category);
      setDescription(item.description);
      setGender(item.gender);
      setPicture(item.productPic);
      setPrice(item.price);

  }

  return (
      <div>
    <div className="container-fluid bg-dark">
    <div className="row collapse show g-0 d-flex h-100">
        <div className="col-3 px-0 w-sidebar navbar-collapse collapse d-none d-md-flex">

            <a href="#" className="navbar-brand">Brand</a>
        </div>
        <div className="col px-2 px-md-0 py-3">
            <div className="d-flex">

                <a data-bs-toggle="collapse" href="#" data-bs-target=".collapse" role="button" className="">
                    <i className="fa fa-bars fa-lg"></i>
                </a>
                <a href="#modal" data-bs-target="modal" data-bs-toggle="modal" className="ms-auto text-white"><i className="fa fa-cog"></i></a>
            </div>
        </div>
    </div>
</div>
<div className="container-fluid px-0">
    <div className="row vh-100 collapse show g-0 d-flex">
        <div className="col-3 p-0 h-100 text-white w-sidebar navbar-collapse collapse d-none d-md-flex sidebar">

            <div className="navbar-dark bg-dark position-fixed h-100 w-sidebar">
                <h6 className="px-3 pt-3">Fixed Menu</h6>
                <ul className="nav flex-column flex-nowrap text-truncate">
                    <li className="nav-item">
                        <a className="nav-link active" href="#">Active</a>
                    </li>
                    <li className="nav-item">
                        <a className="nav-link" href="#">Link</a>
                    </li>
                    <li className="nav-item">
                        <a className="nav-link" href="#">Link</a>
                    </li>
                </ul>
            </div>
        </div>
        <div className="col p-3 text-black">
            <h3>Content..</h3>
            <p>Sriracha biodiesel taxidermy organic post-ironic, Intelligentsia salvia mustache 90's code editing brunch. Butcher polaroid VHS art party, hashtag Brooklyn deep v PBR narwhal sustainable mixtape swag wolf squid tote bag. Tote bag cronut semiotics, raw denim deep v taxidermy messenger bag. Tofu YOLO Etsy, direct trade ethical Odd Future jean shorts paleo. Forage Shoreditch tousled aesthetic irony, street art organic Bushwick artisan cliche semiotics ugh synth chillwave meditation. Shabby chic lomo plaid vinyl chambray Vice. Vice sustainable cardigan, Williamsburg master cleanse hella DIY 90's blog.</p>
            <p>Ethical Kickstarter PBR asymmetrical lo-fi. Dreamcatcher street art Carles, stumptown gluten-free Kickstarter artisan Wes Anderson wolf pug. Godard sustainable you probably haven't heard of them, vegan farm-to-table Williamsburg slow-carb readymade disrupt deep v. Meggings seitan Wes Anderson semiotics, cliche American Apparel whatever. Helvetica cray plaid, vegan brunch Banksy leggings +1 direct trade. Wayfarers codeply PBR selfies. Banh mi McSweeney's Shoreditch selfies, forage fingerstache food truck occupy YOLO Pitchfork fixie iPhone fanny pack art party Portland.</p>
        </div>
    </div>
</div>
