import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import { Base_Url } from "../config/config";
import axios from "axios";
import { NavLink } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";

export const Product = () => {
  const [product, setProduct] = useState({});

  // console.log('cart in product',cartItems)

  const dispatch = useDispatch();
  const navigate = useNavigate();

  const cartItems = useSelector((state) => state.cartReducer);
  const user = useSelector((state) => state.userReducer);

  const location = useLocation();
  const id = location.state.id;
  const [loading, setLoading] = useState(false);

  const HEADER = {
    headers: {
      "Content-Type": "application/json",
      Authorization: "Bearer " + JSON.parse(localStorage.getItem("token")),
    },
  };

  const addToCart = () => {
    let cartProduct = {
      name: product.productName,
      description: product.description,
      category: product.category,
      pic: product.productPic,
      price: product.productPrice,
      quantity: 1,
      id: product._id,
      totalPrice: product.productPrice,
    };
    console.log("CALLING", cartProduct);
    dispatch({ type: "Cart_Update", payload: cartProduct });
    console.log("ADDDD", user, cartItems);
    navigate("/cart");
  };

  useEffect(() => {
    setLoading(true);

    axios
      .get(`${Base_Url}/product/` + id, HEADER)
      .then((result) => {
        // console.log('res',result)
        setProduct(result.data);
        setLoading(false);
      })
      .catch((err) => {
        // console.log('loading',err.response.data)
        setLoading(false);
      });
  }, []);

  return (
    <div>
      {loading ? (
        <div className="d-flex justify-content-center align-items-center spinner">
          <div className="spinner-border" role="status"></div>
        </div>
      ) : (
        ""
      )}
      <div className="main container mt-3 pb-3">
        <div className="row gy-2">
          <div
            className="col-12 col-md-4 border-secondary pb-3"
            style={{ height: "75vh" }}
          >
            <img
              src={product.productPic}
              height="100%"
              width="100%"
              style={{ objectFit: "cover" }}
            />
          </div>

          <div className="col-12 col-md-4">
            <div className="card">
              <h3 className="card-header text-center">{product.productName}</h3>
              <div className="card-body">
                <div className="d-flex justify-content-between">
                  <p className="m-1">reviews</p>
                  <p className="m-1">$</p>
                </div>
                <hr className="m-1" />
                <div className="d-flex justify-content-between">
                  <p className="m-1">price</p>
                  <p className="m-1">${product.productPrice}</p>
                </div>
                <hr className="m-1" />
                <div className="d-flex justify-content-between ">
                  <p className="m-1">Description: </p>
                  <p className="m-1">{product.description}</p>
                </div>
              </div>
            </div>
          </div>

          <div className="col-12 col-md-4">
            <div className="card">
              <h3 className="card-header text-center">Summary</h3>
              <div className="card-body">
                <div className="d-flex justify-content-between">
                  <p className="m-1">Cost</p>
                  <p className="m-1">${product.productPrice}</p>
                </div>
                <hr className="m-1" />
                <div className="d-flex justify-content-between">
                  <p className="m-1">Shipping</p>
                  <p className="m-1">$5</p>
                </div>
                <hr className="m-1" />

                <div className="text-center">
                  <button className="btn btn-warning" onClick={addToCart}>
                    Add to Cart
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
        <div className="row">
          <div className="col-12">
            <h2>Reviews</h2>
            <div>
              <p className="alert alert-info">There is no review.</p>
            </div>

          </div>
        </div>
        <hr className="m-1" />
        <div className="row mt-4">
          <div className="col-12">
            <h2>Write a customer review</h2>
          </div>
        </div>
        <div className="row mt-3">
          <div className="col-12">
            <h3>Rating</h3>
            <select className="form-select">
            <option>
               choose...
              </option>
              <option>
                1 - Poor
              </option>
              <option>
               2 - Unsatisfactory
              </option>
              <option>
                3 - Satisfactory
              </option>
              <option>
               4 - Very Satisfactory
              </option>
              <option>
                5 - Outstanding
              </option>
            </select>
          </div>
        </div>
        <div className="row mt-3">
          <div className="col-12">
          <label for="floatingTextarea">Comments</label>
            <textarea className="form-control" id="floatingTextarea">

            </textarea>

          </div>
        </div>
      </div>
    </div>
  );
};
