import React from "react";
import {
    PayPalScriptProvider,
    PayPalButtons,
    usePayPalScriptReducer
} from "@paypal/react-paypal-js";
import { NavLink } from "react-router-dom";
import "./styles.css";

const amount = "2";
const currency = "USD";
const style = {"layout":"vertical"};
const Receipt = () => {

    const onCancel = (data) => {
        console.log("The payment was cancelled!", data);
      };

  return (
    <div>
      <div className="ms-4 d-inline-flex container-fluid mt-5 mb-5">
        <div className="row w-50   text-center mb-2 me-1">
          <div className="row  text-center mb-2  border border-1">
            <div className="col-12 p-0 ">
              <h3 className="light p-2 m-0">Shipping</h3>
            </div>
          </div>

          <div className="row    text-center mb-2 border border-1">
            <div className="col-12 p-0 ">
              <h3 className="light p-2 m-0">Payment</h3>
              <p>Method: Paypal</p>
            </div>
          </div>

          <div className="row  text-center mb-2 border border-1">
            <div className="col-12 p-0 ">
              <h3 className="light p-2 m-0">Items</h3>
            </div>
          </div>
        </div>

        <div className="row  gy-2 w-50 ">
          <div className="col-12 col-md-12">
            <div className="card">
              <h3 className="card-header text-center">Order Summary</h3>
              <div className="card-body">
                <div className="d-flex justify-content-between">
                  <p className="m-1">Cost</p>

                  <p className="m-1">$</p>
                </div>
                <div className="d-flex justify-content-between">
                  <p className="m-1">Shipping</p>
                  <p className="m-1">$5</p>
                </div>
                <hr className="m-1" />
                <div className="d-flex justify-content-between fw-bold">
                  <p className="m-1">Total</p>
                  <p className="m-1">$</p>
                </div>
                <div className="text-center">
                  <PayPalScriptProvider options={{ "client-id": "test" }}>
                    <PayPalButtons
                       style={style}
                       disabled={false}
                       forceReRender={[amount, currency, style]}
                       fundingSource={undefined}

          onCancel={onCancel}
                      createOrder={(data, actions) => {
                        return actions.order.create({
                          purchase_units: [
                            {
                              amount: {
                                value: "2.00",
                              },
                            },
                          ],
                        });
                      }}
                    />
                  </PayPalScriptProvider>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Receipt;
