const initialState = {
    cart:{}
}
const cartReducer = (state = initialState, action) => {
    switch(action.type) {
        case "Cart_Update": return{ state, cart:action.payload };
        case "Cart_Empty": return{ initialState };
        default: return{ initialState };
    }

}

export default cartReducer
