// const initialState = {
//     cart:[{}]
// }
const cartReducer = (state, action) => {

    let updatedCart = state.cart
    updatedCart.push(action.payload)
    console.log(state.cart,updatedCart)

    switch(action.type) {
        case "Cart_Update": return{ 
            state, 
            cart:state.cart.push(action.payload)
        };
        case "Cart_Empty": return{ ...state, cart:[] };
        default: return{ ...state, cart:[] };
    }

}

export default cartReducer
