const initialState = {
    cart:[]
}

const cartReducer = (state=initialState, action) => {

    console.log('inside cart',state.cart)
    // let  updateCart = [...state.cart]
    //  updateCart.concat(action.payload)
    //  console.log(updateCart)
    switch(action.type) {
        case "Cart_Update": return{ 
            ...state, 
            // cart: [...state.cart,action.payload]
            cart: state.cart.map(product =>
                product._id === action.payload.id ? {...cart, selected: true} : product,
              ),
        };
        case "Cart_Empty": return{ initialState };
        default: return state;
    }

}

export default cartReducer
