import React, { useEffect, useState } from 'react'

const Profile = () => {

    const [user, setUser] = useState({})
    const [email, setEmail] = useState("");
  const [firstname, setFirstname] = useState("");
  const [lastname, setLastname] = useState("");
  const [password, setPassword] = useState("");
  const [newpassword, setNewPassword] = useState("");
  const [edit, setEdit] = useState(false)

    useEffect(()=>{
        const userData = JSON.parse(localStorage.getItem("user"))
        setUser(userData)
        setFirstname(userData.firstName)
        setLastname(userData.lastName)
        setEmail(userData.email)

    },[])

  return (
    <div>
        <div className="container mt-5 mb-5">
            <h3 className="mb-5">My Profile </h3>
            {
                    user?
            <div className="row">

                    <div className="col-12">
                    <h6>First Name:</h6>
                    {
                        edit?

                    <input type="text" className="form-control mb-3" value={firstname} onChange={(e) => setFirstname(e.target.value)}/>
                   : <p>{firstname}</p>
                    }
                </div>
                <div className="col-12">
                    <h6>Last Name:</h6>
                    {
                        edit?
                    <input type="text" className="form-control mb-3" value={lastname} onChange={(e) => setLastname(e.target.value)}/>
                :<p>{lastname}</p>
                    }
                </div>
                <div className="col-12">
                    <h6>Email:</h6>
                    {
                        edit?
                    <input type="email" className="form-control mb-3" value={email} onChange={(e) => setEmail(e.target.value)}/>
               : <p>{email}</p>
                    }
                </div>
                {
                    edit?
<div className="col-12">
                    <h6>New Password</h6>

                    <input type="text" className="form-control mb-3" value={password} onChange={(e) => setPassword(e.target.value)}/>
                </div>:""
                }

                {
                    edit?
<div className="col-12">
                    <h6>Confirm Password</h6>
                    <input type="text" className="form-control mb-3" value={newpassword} onChange={(e) => setNewPassword(e.target.value)}/>
                </div>
                :""}


            </div>
            :""
        }
        </div>

    </div>
  )
}

export default Profile
