import React, { useEffect, useState } from "react";
import axios from "axios";
import { Base_Url } from "../config/config";
import { Link } from "react-router-dom";

export const Dashboard = () => {
    const [productList, setProductList] = useState([]);
    const [loading, setLoading] = useState(false);
    const HEADER = {
        headers: {
          "Content-Type": "application/json",
          Authorization: "Bearer " + JSON.parse(localStorage.getItem("token")),
        },
      };

    useEffect(() => {
        setLoading(true);
        loadProducts();
    },[]);

    const loadProducts = () => {
        axios
          .get(`${Base_Url}/allProducts`, HEADER)
          .then((result) => {
            console.log("res", result);

            setProductList(result.data);
            setLoading(false);

          })
          .catch((err) => {
            // console.log("loading", err.response.data);
            setLoading(false);
            //Swal.fire(err.response.data.error);
          });
      };

    return (
      <div className="container d-flex justify-content-center mt-5 mb-5">
        {loading ? (
          <div className="d-flex justify-content-center align-items-center spinner">
            <div className="spinner-border" role="status"></div>
          </div>
        ) : (
          ""
        )}
          {
          productList.length ?
        <div className=" row  g-4">
          {productList.map((item, index) => {
            return (
              <div className="col-lg-4 col-md-4" key={index}>
                <div className="card h-100">
                  <img
                    src={item.productPic}
                    className="card-img-top h-75"
                    alt="..."
                  />
                  <div className="card-body">
                    <h5 className="card-title">{item.productName}</h5>
                    <p className="card-text">{item.description}</p>
                  </div>
                  <button
                    className="btn btn-warning m-3"
                    data-bs-toggle="modal" data-bs-target="#staticBackdrop"
                  >
                        Edit Product
                  </button>
                </div>
              </div>
            );
          })}


        </div>
  :
  <p className="text-center mt-5 text-danger">Sorry, there are no products to display!</p>
  }

<div className="modal fade" id="staticBackdrop" data-bs-backdrop="static" data-bs-keyboard="false" tabIndex="-1" aria-labelledby="staticBackdropLabel" aria-hidden="true">
  <div className="modal-dialog modal-dialog-scrollable modal-dialog-centered">
    <div className="modal-content">
      <div className="modal-header">
        <h1 className="modal-title fs-5" id="staticBackdropLabel">Edit Product</h1>
        <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
      </div>
      <div className="modal-body">
      <form
        className="row w-50 g-3 border border-3 p-3"
        onSubmit={formSubmitted}
      >
        <h3>New Product</h3>
        <div className="col-12">
          <label htmlFor="name" className="form-label">
            Name
          </label>
          <input
            type="text"
            className="form-control"
            id="name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </div>
        <div className="col-12">
          <label htmlFor="validationCustom04" className="form-label">
            Category
          </label>
          <select
            className="form-select"
            id="validationCustom04"
            value={category}
            onChange={(e) => setCategory(e.target.value)}
            required
          >
            <option disabled value="DEFAUL">
              Choose...
            </option>
            <option>Shirt</option>
            <option>Pant</option>
          </select>
        </div>
        <div className="col-12">
          <label htmlFor="inputAddress" className="form-label">
            Price
          </label>
          <input
            type="number"
            className="form-control"
            id="inputAddress"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
            required
          />
        </div>

        <div className="col-12">
          <label htmlFor="inputCity" className="form-label">
            Picture URL
          </label>
          <input
            type="text"
            className="form-control"
            id="inputCity"
            value={picture}
            onChange={(e) => setPicture(e.target.value)}
            required
          />

            <div className="border border-1 mt-1" style={{ width: 150, height: 200 }}>
            {picture!=""?
          <img
            src={picture}
            className="img-thumbnail"
            style={{ width: 150, height: 200 }}
          />:<p className="text-center">Image Preview</p>
        }
          </div>


        </div>
        <div className="col-12">
          <label htmlFor="inputState" className="form-label">
            Gender
          </label>
          <select
            className="form-select"
            id="validationCustom04"
            value={gender}
            onChange={(e) => setGender(e.target.value)}
            required
          >
            <option disabled value="DEFAULT">
              Choose...
            </option>
            <option>men</option>
            <option>women</option>
          </select>
        </div>
        <div className="col-12">
          <label htmlFor="inputZip" className="form-label">
            Description
          </label>
          <input
            type="text"
            className="form-control"
            id="inputZip"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            required
          />
        </div>

        <div className="col-12">
          {/* <Link to="/payment" type="submit" > */}
          <button type="submit" className="btn btn-warning">
            Save Product
          </button>
          {/* </Link> */}
        </div>
      </form>
      </div>
      <div className="modal-footer">
        <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Close</button>
        <button type="button" className="btn btn-primary">Understood</button>
      </div>
    </div>
  </div>
</div>
      </div>

    );
}

export default Dashboard;
