    }

  return (
    <div>
        <div className="container mt-5 mb-5">
            <h3 className="mb-5">My Profile </h3>
            {
                    user?
            <div className="row">
                <form  onSubmit={submitHandle}>

                    <div className="col-12">
                    <h6>First Name:</h6>
                    {
                        edit?

                    <input type="text" className="form-control mb-3" value={firstname} onChange={(e) => setFirstname(e.target.value)}/>
                   : <p>{firstname}</p>
                    }
                </div>
                <div className="col-12">
                    <h6>Last Name:</h6>
                    {
                        edit?
                    <input type="text" className="form-control mb-3" value={lastname} onChange={(e) => setLastname(e.target.value)}/>
                :<p>{lastname}</p>
                    }
                </div>
                <div className="col-12">
                    <h6>Email:</h6>
                    {
                        edit?
                    <input type="email" className="form-control mb-3" value={email} onChange={(e) => setEmail(e.target.value)}/>
               : <p>{email}</p>
                    }
                </div>
                {
                    edit?
