import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import { Base_Url } from "../config/config";
import axios from "axios";
import { NavLink } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";
import Swal from "sweetalert2";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faStar as faStar} from "@fortawesome/free-solid-svg-icons";
import { faStar as star } from '@fortawesome/free-regular-svg-icons';
import { faStarHalfStroke } from "@fortawesome/free-solid-svg-icons";

import moment from "moment";
import { useParams } from "react-router";

export const Product = () => {
  const [product, setProduct] = useState({});
  const [reviewData, setReview] = useState([]);

  const dispatch = useDispatch();
  const navigate = useNavigate();
  const params = useParams();
  console.log("PPPaaa", params);
  // const cartItems = useSelector((state) => state.cartReducer);
  const user = useSelector((state) => state.userReducer);
  const userId = user.user.id || null;
  // console.log("userpppp",user)

  const [loading, setLoading] = useState(false);
  const [rating, setrating] = useState("default");
  const [comment, setComment] = useState("");
  const [userReview, setUserReview] = useState("");
  const [avg, setAvg] = useState(0);
  const [decimal, setDecimal] = useState(0);

  const location = useLocation();

  const HEADER = {
    headers: {
      "Content-Type": "application/json",
      Authorization: "Bearer " + JSON.parse(localStorage.getItem("token")),
    },
  };

  const addToCart = () => {
    let cartProduct = {
      name: product.productName,
      description: product.description,
      category: product.category,
      pic: product.productPic,
      price: product.productPrice,
      quantity: 1,
      id: product._id,
      totalPrice: product.productPrice,
      itemQuantity: product.quantity
    };
    if(product.quantity >0){
      dispatch({ type: "Cart_Update", payload: cartProduct });

      navigate("/cart");
    }
    else{
      Swal.fire("You cannot add an out of stock product to cart");
    }


  };

  useEffect(() => {
    setLoading(true);

    getProduct();
  }, [location?.state, user.user]);

  const getProduct = () => {
    console.log("called product", location, location.state);
    if (location?.state !== null) {
      axios
        .get(
          `${Base_Url}/product/` + location?.state?.id + "/" + userId,
          HEADER
        )
        .then((result) => {
          console.log("res", result.data.review, result.data);
          setLoading(false);
          setProduct(result.data);
          setReview(result.data.review);
          var avgRating;
          if(result.data.review.length){
            avgRating = result.data.review.reduce((total,item)=> total + item?.rating, 0) / result.data.review.length
            setAvg(avgRating);
          }
          else{
            avgRating = 0
            setAvg(0);
          }




          const decimalPart = avgRating.toString().split('.')[1];
          setDecimal(decimalPart);
          const index = result.data.review.findIndex((item) =>item.reviewedBy._id === userId)

          if(index>=0){
            setComment(result.data.review[index].comment);
              setrating(result.data.review[index].rating);
              setUserReview(result.data.review[index]._id);
          }
          else{
            setComment("");
            setrating("default");
            setUserReview("");
          }
       })
        .catch((err) => {
          // console.log('loading',err.response.data)
          setLoading(false);
        });
    } else {
      setLoading(false);
    }
  };

  const submitted = (event) => {
    event.preventDefault();
    if (!Object.keys(user.user).length) {
      Swal.fire({
        title: "Please login to leave a review",
        icon: "warning",
      });

      return;
    }
    if (rating == "default" || comment == "") {
      Swal.fire("Please fill all the fields");
      return;
    }
    let review = {
      rating: Number(rating),
      comment: comment,
      date: moment(new Date()).format("YYYY-MM-DD"),
    };
    axios
      .put(`${Base_Url}/productReview/` + location?.state?.id, review, HEADER)
      .then((result) => {
        console.log("res", result.data);
        setLoading(false);
        Swal.fire({
          title: result.data.message,
          icon: "success",
        }).then(() => {
          setrating("default");
          setComment("");
          getProduct();
        });
      })
      .catch((err) => {
        console.log("loading", err.response.data);
        setLoading(false);
      });
  };

  const editReview = () => {
    console.log("iD", userReview, HEADER);
    if (rating == "default" || comment == "") {
      Swal.fire("Please fill all the fields");
      return;
    }
    let updatedReview = {
      rating: Number(rating),
      comment: comment,
      date: moment(new Date()).format("YYYY-MM-DD"),
    };
    axios
      .put(
        `${Base_Url}/editReview/` + location?.state?.id + "/" + userReview,
        updatedReview,
        HEADER
      )
      .then((result) => {
        console.log("res", result.data);
        setLoading(false);
        Swal.fire({
          title: result.data.message,
          icon: "success",
        }).then(() => {
          setrating("default");
          setComment("");
          setUserReview("");
          getProduct();
        });
      })
      .catch((err) => {
        console.log("loading", err.response.data);
        setLoading(false);
      });
  };
  const deleteReview = () => {
    console.log("iD", userReview);
    axios
      .delete(
        `${Base_Url}/deleteReview/` + location?.state?.id + "/" + userReview,
        HEADER
      )
      .then((result) => {
        console.log("res", result.data);
        setLoading(false);
        Swal.fire({
          title: result.data.message,
          icon: "success",
        }).then(() => {
          setrating("default");
          setComment("");
          setUserReview("");
          getProduct();
        });
      })
      .catch((err) => {
        console.log("loading", err.response.data);
        setLoading(false);
      });
  };

  return (
    <div>
      {loading ? (
        <div className="d-flex justify-content-center align-items-center spinner">
          <div className="spinner-border" role="status"></div>
        </div>
      ) : (
        ""
      )}
      <div className="main container mt-3 pb-3">
        <div className="row gy-2">
          <div
            className="col-12 col-md-4 border-secondary pb-3"
            style={{ height: "75vh" }}
          >
            <img
              src={product.productPic}
              height="100%"
              width="100%"
              style={{ objectFit: "cover" }}
            />
          </div>

          <div className="col-12 col-md-4">
            <div className="card">
              <h3 className="card-header text-center">{product.productName}</h3>
              <div className="card-body">
                <div className="d-flex justify-content-between">
                  <p >Reviews</p>{avg}
                  <div>
                  {[...Array(5)].map((_, i) => {
                          return (

                              i < avg ?
                              <FontAwesomeIcon icon={faStar}  style={{ color: "#ffe11f" }} key={i}/>
                              :
                              i==avg && decimal ? <FontAwesomeIcon icon={faStarHalfStroke} style={{ color: "#ffe11f" }} /> :

                            <FontAwesomeIcon icon={star} style={{ color: "#ffe11f" }} key={i}/>

                          )
                        })}
                  </div>

                </div>
                <hr className="m-1" />
                <div className="d-flex justify-content-between">
                  <p className="m-1">Price</p>
                  <p className="m-1">${product.productPrice}</p>
                </div>
                <hr className="m-1" />
                <div className="d-flex justify-content-between ">
                  <p className="m-1">Description: </p>
                  <p className="m-1">{product.description}</p>
                </div>
                <p className="text-danger">*Out of stock</p>
              </div>
            </div>
          </div>

          <div className="col-12 col-md-4">
            <div className="card">
              <h3 className="card-header text-center">Summary</h3>
              <div className="card-body">
                <div className="d-flex justify-content-between">
                  <p className="m-1">Cost</p>
                  <p className="m-1">${product.productPrice}</p>
                </div>
                <hr className="m-1" />
                <div className="d-flex justify-content-between">
                  <p className="m-1">Shipping</p>
                  <p className="m-1">$5</p>
                </div>
                <hr className="m-1" />

                <div className="text-center">
                  <button className="btn btn-warning" onClick={addToCart}>
                    Add to Cart
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
        <div className="row">
          <div className="col-12">
            <h2>Reviews</h2>
            {!reviewData?.length?
            <div className="alert alert-info">
              <p >There is no review</p>
            </div>:""
}
            <hr className="m-1" />
            <div>
              {reviewData
                .slice()
                .reverse()
                .map((item, index) => {
                  return (
                    <div className="row border-bottom" key={index}>
                      <div className="col-12 mt-2">
                        {item.reviewedBy.firstName}
                      </div>

                      <div>
                        {[...Array(item.rating)].map((_, i) => {
                          return (
                            <FontAwesomeIcon
                              icon={faStar}
                              style={{ color: "#ffe11f" }}
                              key={i}
                            />
                          );
                        })}
                      </div>
                      <div className="col-12">{item.date}</div>
                      <div className="col-12 mt-3 mb-1">{item.comment}</div>
                    </div>
                  );
                })}
            </div>
            {/* <hr className="m-1" /> */}
            <div className="row mt-4">
              <div className="col-12">
                <h2>Write a customer review</h2>
              </div>
            </div>
          </div>
        </div>
        <h3>Rating</h3>
        <form className="row g-3" onSubmit={submitted}>
          <div className="col-12">
            <select
              className="form-select"
              value={rating}
              onChange={(e) => setrating(e.target.value)}
            >
              <option value="default">choose...</option>
              <option value="1">1 - Poor</option>
              <option value="2">2 - Unsatisfactory</option>
              <option value="3">3 - Satisfactory</option>
              <option value="4">4 - Very Satisfactory</option>
              <option value="5">5 - Outstanding</option>
            </select>
          </div>
          <div className="col-12">
            <div className="form-floating">
              <textarea
                className="form-control"
                placeholder="Leave a comment here"
                id="floatingTextarea2"
                value={comment}
                style={{ height: 100 }}
                onChange={(e) => setComment(e.target.value)}
              ></textarea>
              <label htmlFor="floatingTextarea2">Comments</label>
            </div>
          </div>
          {userReview === "" ? (
            <div className="col-12">
              <button
                type="submit"
                className="btn btn-warning"
                disabled={!rating && !comment}
              >
                Submit
              </button>
            </div>
          ) : (
            <div className="col-12">
              <button
                type="button"
                className="btn btn-warning"
                onClick={() => editReview()}
              >
                Edit
              </button>
              <button
                type="button"
                className="btn btn-warning ms-2"
                onClick={() => deleteReview()}
              >
                Delete
              </button>
            </div>
          )}
        </form>
      </div>
    </div>
  );
};
