const initialState = {
    cart:[]
}

const cartReducer = (state=initialState, action) => {

    console.log('inside cart',state.cart,state, action.type,action.payload)

    // let  updateCart = [...state.cart]
    //  updateCart.concat(action.payload)
    //  console.log(updateCart)
    switch(action.type) {
        case "Cart_Update": return{ ...state,  
                                    // cart: state.cart.concat(action.payload)
                                    cart: state.cart.filter(item=>{
                                        console.log('opop')
                                        if(item.id===action.payload.id){
                                            item.quantity += action.payload.quantity
                                        }
                                        else{
                                            state.cart.concat(action.payload)
                                        }
                                    })

                                };
        case "Cart_Empty": return{ ...state, cart:[] };
        default: return { ...state, cart:[] };
    }

}

export default cartReducer
