import React, { useEffect, useState } from "react";
import axios from "axios";
import Swal from "sweetalert2";
import "./styles.css";
import { Base_Url } from "../config/config";

export const Topsale = () => {

    const [salesList, setSaleList] = useState([]);
    const [loading, setLoading] = useState(false);

    useEffect (()=> {
        setLoading(true)
        console.log('loadi',loading)
        loadSales();

      },[]);

      const loadSales = () => {
        axios
        .get(`${Base_Url}/topSales`)
        .then((result) => {
          setLoading(true)
            console.log(result.data.salesList,result)
              setLoading(false);
              setSaleList(result.data.salesList);
              console.log('list',salesList)
        })
        .catch((err) => {
          console.log('err',err)
          setLoading(false);
          Swal.fire(err.response.data.error);
        });

      }

    return (
        <div className="container mt-5">
            { loading ? <div className="d-flex justify-content-center align-items-center spinner">
        <div className="spinner-border" role="status"></div>
      </div>: '' }
            <h3 className="text-center fw-bold pb-4">
                TOP 5 SALES
            </h3>
            <table className="table">
                <thead>
                    <tr>
                        <th scope="col" >#</th>
                        <th scope="col" >Sales Id:</th>
                        <th scope="col">Product Name</th>
                        <th scope="col">Quantity</th>
                        <th scope="col">Sale Amount</th>
                    </tr>
                </thead>
                <tbody className="table-group-divider">
{/*                    
