import React, { useEffect, useState } from "react";
import { useLocation } from 'react-router-dom';
import { Base_Url } from "../config/config";
import axios from "axios";
import { NavLink } from "react-router-dom";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faTrashCan } from "@fortawesome/free-solid-svg-icons";
import './styles.css'

const Cart = () => {

    const initialCount = 1;
    const [product, setProduct] = useState();
    const [count=initialCount,setCount] = useState();
  const location = useLocation();
  const id = location.state.id

  const [loading, setLoading] = useState(false);


  const HEADER = {
    headers:{
      "Content-Type": "application/json",
      "Authorization": 'Bearer ' + JSON.parse(localStorage.getItem("token") )  
    }
  }

  useEffect(() => {
    setLoading(true);

    axios
      .get(`${Base_Url}/product/`+id,HEADER)
      .then((result) => {
        setProduct(result.data);
        // if(product){
        //     setCount(count+1);
        // }
        priceUpdate = result.productPrice
        setLoading(false);
      })
      .catch((err) => {
        //console.log('loading',err.response.data)
        setLoading(false);
      });
console.log('sss',count)
// setCount(count)
if(product && count>1){
    let productUpdate = product
    productUpdate.productPrice = count*product.productPrice
    setProduct({...product,productUpdate})
    console.log('count increased', product)
}

  }, [count]);

  const handleChange = (event)=>{
    console.log('cccc',event.target.value)
  }
  const increaseCount = ()=>{
    //   let val = count+1
    //   count = count+1
       setCount(count+1)

      console.log("Ca",count)

    // product.productPrice = count*product.productPrice
    // console.log(product.productPrice)

  }

  const deleteItem = ()=>{
      setProduct() 
      setCount(0)
      console.log('p',product)
  }

  const decreaseCount = ()=>{
      if(count>1){
        setCount(count-1)
        product.productPrice = product.productPrice/count
      }



}

  return (
    <div>
        <div className="main container mt-3 pb-3">
            <div className="row gy-2">
                <div className="col-12 col-md-6 border border-1 border-secondary pb-3">
                    <div className="row  text-center mb-2 ">
                        <div className="col-12 p-0 ">
                            <h3 className="light p-2 m-0">Items in cart</h3>
                        </div>
                    </div>


                    <div className="row justify-content-center">
                    {
                    product ? <div className="col-12 col-md-12 col-lg-4  text-center">
                            <img src={product.productPic} height="170" width="150" />
                        </div>:""
                        }
                        {
                    product?
                        <div className="col-12 col-md-12 col-lg-3  text-center">
                            <p>{product.productName}</p>
                            <p>${product.productprice}</p>
                            <button className="btn btn-warning">
                            <FontAwesomeIcon className="trash" icon={faTrashCan} onClick={deleteItem}/>
                                </button>
                        </div>:""
                        }
                        <div className="col-8 col-md-12 col-lg-5   ">
                            <div className="d-flex">
                                <button className="btn btn-warning " onClick={decreaseCount} disabled={!product}>-</button>
                                <input type="number" value={count} className="form-control  me-1 ms-1" onChange={handleChange} disabled={!product} />
                                <button className="btn btn-warning " onClick={increaseCount} disabled={!product}>+</button>
                            </div>

                        </div>
                    </div>
                </div>
                <div className="col-12 col-md-6">
                    <div className="card">
                        <h3 className="card-header text-center">Summary</h3>
                        <div className="card-body">
                            <div className="d-flex justify-content-between">
                                <p className="m-1">Cost</p>
                                {
                    product?  <p className="m-1">${product.productPrice}</p>:""
                                }
                            </div>
                            <div className="d-flex justify-content-between">
                                <p className="m-1">Shipping</p>
                                <p className="m-1">$5</p>
                            </div>
                            <hr className="m-1" />
                            <div className="d-flex justify-content-between fw-bold">
                                <p className="m-1">Total</p>
                                {
                    product?    <p className="m-1">$20</p>:""
                                }
                            </div>
                            <div className="text-center">
                                <button href="#" className="btn btn-warning" disabled={!product} >Checkout</button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>

        </div>
    </div>
  )
}

export default Cart
