const initialState = {
    cart:[]
}

const cartReducer = (state, action) => {

    console.log('inside cart',state.cart)
    const item = state.cart.find((item)=>item.id===action.payload.id)
    if (item){
        item.quantity++;
    }
    else{
        state.cart.push({...action.payload})
    }
    // let  updateCart = [...state.cart]
    //  updateCart.concat(action.payload)
    //  console.log(updateCart)
    switch(action.type) {
        case "Cart_Update": return{ 
            ...state, 
            cart: state.cart.find((item)=>item.id===action.payload.id ? item.quantity=action.payload.quantity : state.cart.concat(action.payload))

        };
        case "Cart_Empty": return{ ...state, cart:[] };
        default: return state;
    }

}

export default cartReducer
