import React from "react";
import { Link } from "react-router-dom";
import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import Swal from "sweetalert2";
import { Base_Url } from "../config/config";
import axios from "axios";

export const Dashboard = () => {
    const [name, setName] = useState("");
  const [productList, setProductList] = useState([]);
  const [loading, setLoading] = useState(false);
  const [category, setCategory] = useState("DEFAUL");
  const [price, setPrice] = useState(0);
  const [picture, setPicture] = useState("");
  const [gender, setGender] = useState("DEFAULT");
  const [description, setDescription] = useState("");
  const HEADER = {
    headers: {
      "Content-Type": "application/json",
      Authorization: "Bearer " + JSON.parse(localStorage.getItem("token")),
    },
  };

  useEffect(() => {
    setLoading(true);
    loadProducts();
  }, []);

  const loadProducts = () => {
    axios
      .get(`${Base_Url}/allProducts`, HEADER)
      .then((result) => {
        console.log("res", result);

        setProductList(result.data);
        setLoading(false);
      })
      .catch((err) => {
        // console.log("loading", err.response.data);
        setLoading(false);
        //Swal.fire(err.response.data.error);
      });
  };
  const formSubmitted = (event) => {
    console.log('nnn',name)
    event.preventDefault();
    if (
      name === "" ||
      category === "" ||
      price === "" ||
      picture === "" ||
      gender === "" ||
      description === ""
    ) {
      Swal.fire("Fields should not be empty");
      return;
    }

    setLoading(true);
    let product = {
      productName: name,
      gender: gender,
      productPrice: price,
      productPic: picture,
      category: category,
      description: description,
    };
    axios
      .post(`${Base_Url}/addProduct`, product, HEADER)
      .then((result) => {
        setLoading(false);
        if (result.status === 200) {
          Swal.fire({
            title:result.data.message,
          icon: "success"
        }).then(()=>{
          console.log("sub");
            setName("")
            setCategory("DEFAUL")
            setDescription("")
            setGender("DEFAULT")
            setPrice("") 
            setPicture("")
          })

        }
      })
      .catch((err) => {
        setLoading(false);
        Swal.fire({
          icon: "error",
          title: err.response.data.error,
        });
      });
  };

  const editProduct = (item)=>{
      console.log(item)
      setName(item.productName);
      setCategory(item.category);
      setDescription(item.description);
      setGender(item.gender);
      setPicture(item.productPic);
      setPrice(item.price);

  }

  return (

    <div class="container-fluid px-0">
    <div class="row vh-100 collapse show g-0 d-flex">
        <div class="col-3 p-0 h-100 text-white w-sidebar navbar-collapse collapse d-none d-md-flex sidebar">

            <div class="navbar-dark bg-dark position-fixed h-100 w-sidebar">
                <h6 class="px-3 pt-3">Fixed Menu</h6>
                </div>
            <div class="col p-3" >
            <div className="container d-flex justify-content-center mt-5 mb-5">
      {loading ? (
        <div className="d-flex justify-content-center align-items-center spinner">
          <div className="spinner-border" role="status"></div>
        </div>
      ) : (
        ""
      )}
      {productList.length ? (
        <div className=" row  g-4">
          {productList.map((item, index) => {
            return (
              <div className="col-lg-4 col-md-4" key={index}>
                <div className="card h-100">
                  <img
                    src={item.productPic}
                    className="card-img-top h-75"
                    alt="..."
                  />
                  <div className="card-body">
                    <h5 className="card-title">{item.productName}</h5>
                    <p className="card-text">{item.description}</p>
                  </div>
                  <button
                    className="btn btn-warning m-3"
                    data-bs-toggle="modal"
                    data-bs-target="#staticBackdrop"
                    onClick={()=>editProduct(item)}
                  >
                    Edit Product
                  </button>
                </div>
              </div>
            );
          })}
        </div>
      ) : (
        <p className="text-center mt-5 text-danger">
          Sorry, there are no products to display!
        </p>
      )}

      <div
        className="modal fade"
        id="staticBackdrop"
        data-bs-backdrop="static"
        data-bs-keyboard="false"
        tabIndex="-1"
        aria-labelledby="staticBackdropLabel"
        aria-hidden="true"
      >
        <div className="modal-dialog modal-dialog-scrollable modal-dialog-centered">
          <div className="modal-content">
            <div className="modal-header">
              <h1 className="modal-title fs-5" id="staticBackdropLabel">
                Edit Product
              </h1>
              <button
                type="button"
                className="btn-close"
                data-bs-dismiss="modal"
                aria-label="Close"
              ></button>
            </div>
            <div className="modal-body">
              <form
                className="row w-50 g-3 border border-3 p-3"
                onSubmit={formSubmitted}
              >

                <div className="col-12">
                  <label htmlFor="name" className="form-label">
                    Name
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    id="name"
                    value={name || ''}
                    onChange={(e) => setName(e.target.value)}
                    required
                  />
                </div>
                <div className="col-12">
                  <label htmlFor="validationCustom04" className="form-label">
                    Category
                  </label>
                  <select
                    className="form-select"
                    id="validationCustom04"
                    value={category || ''}
                    onChange={(e) => setCategory(e.target.value)}
                    required
                  >
                    <option disabled value="DEFAUL">
                      Choose...
                    </option>
                    <option>Shirt</option>
                    <option>Pant</option>
                  </select>
                </div>
                <div className="col-12">
                  <label htmlFor="inputAddress" className="form-label">
                    Price
                  </label>
                  <input
                    type="number"
                    className="form-control"
                    id="inputAddress"
                    value={price || 0}
                    onChange={(e) => setPrice(e.target.value)}
                    required
                  />
                </div>

                <div className="col-12">
                  <label htmlFor="inputCity" className="form-label">
                    Picture URL
                  </label>
                  <input
                    type="text"
                    className="form-control"
